import React, {useReducer, useState} from "react"


// function for a set of particular action 
function reducer2(state, action){

    switch(action.type){
        case "add_todo":
            return { 
                // the rendered todo list has the action text and compteled status 
                todos : [...state.todos, {text: action.text, completed : false}]
            }
        case "toggle_todo":
            return { 
                // the rendered todo list has the action text and compteled status 
                todos : state.todos.map((t, i) => i === action.i ? {...t, completed : !t.completed} : t )
            }
        default:
            return state
    }

}


const Usereducer2 = () => {

    //  dispatch => action  type="text"
    const [{todos}, dispatch] = useReducer(reducer2, {todos : [] })
    const [text, setText] = useState()

    return (
        <>
            <form 
                onSubmit={e =>{
                e.preventDefault()
                // payload => text / payload : text 
                dispatch({ type : "add_todo", text })
                setText("")
            }}>
                <input value={text} onChange={e => setText(e.target.value)} />

            </form>

            <pre>{JSON.stringify(todos, null, 2 )}</pre>

{/* use bracket */}
            {todos.map(
                      (t, i) => (
                      <div key={i} onClick={()=> dispatch({type: 'toggle_todo', i})}
                      style={{textDecoration: 'strikethrough'}}
                      > {t.text}</div>
                      )
             )}


        </>
    )

}


export default Usereducer2;
